/* ==UserStyle==
@name           (Unused) Remove sidebars and footer.
@namespace      USO Archive
@author         Alejandro Ramirez
@description    `Remove sidebars and footer.`
@version        20180613.18.25
@license        NO-REDISTRIBUTION
@preprocessor   uso
==/UserStyle== */
@-moz-document domain(stackoverflow.com) {

#left-sidebar {
    display: none; 
}

#sidebar {
    display: none; 
}

#footer {
  display: none;
}

.started {
  display: none;
}

.pl8 {
    display: none;
}

#mainbar {
    width: 100%;
}

}

